import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import "./profile.css"

const Profile = ({ user }) => {
     const [users_gets, setUserGets] = useState(null) 
     const location = useLocation();
     const path = location.pathname.split("/")[1];
     useEffect(() => {
         const getUsers = () => {
             fetch("/api/gets_user", {
                 method: "GET",
                 credentials: 'include',
                 headers: {
                     "Accept": "application/json",
                     "Content-Type": "application/json",
                     "Access-Control-Allow-Credentials": true,
                 }
             }).then((response) => {
                 if(response.status === 200) return response.json()
                 throw new Error("Ошибка")
             }).then((resObject) => {
               resObject.forEach(element => {
                    if(element.userID === path){
                         console.log(element)
                    }
                 })
             }).catch((err) => {
                 console.log(err)
             })
         }
         getUsers()
     },[])
     console.log(users_gets)
     return(
          <div>сделать профиль на подобия стима и тут же сделать на редактирование описания{user.userName}</div>
     )
}

export default Profile;
