import Navbar from './Components/Navbar/Navbar';
import './App.css';
import { useState, useEffect } from 'react';

const App = () => {
    const [userDiscord, serUserDiscord] = useState(null);
    useEffect(() => {
        const getUser = () => {
            fetch('/auth/login/success', {
                method: "GET",
                credentials: 'include',
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json",
                    "Access-Control-Allow-Credentials": true,
                }
            }).then((response) => {
                if(response.status === 200) return response.json()
                throw new Error("Ошибка")
            }).then((resObject) => {
                serUserDiscord(resObject)
            }).catch((err) => {
                console.log(err)
            })
        }
        getUser()
    },[])
    if(userDiscord === null){
        return(
            <BrowserRouter>
                <Navbar user={userTwitch} />
                <Routes>
                    <Route 
                        path="/" 
                        element={<Home key={posts} posts={posts}/>} 
                    />
                    <Route
                        path="/post/:id"
                        element={userTwitch ? <Post key={posts} posts={posts}/> : <Navigate to="/login" />}      
                    />
                    <Route
                        path="/login"
                        element={userTwitch ? <Navigate to="/" /> : <Login />}
                    />
                </Routes>
            </BrowserRouter>
        )
    }
    return (
        <BrowserRouter>
            <Navbar user={users} />
            <Routes>
                <Route 
                    path="/" 
                    element={<Home key={posts} posts={posts} />} 
                />
                <Route 
                    path="/forms/:id" 
                    element={users ? <Forms key={users} user={users} posts={posts}/> : <Navigate to="/login" />}
                />user
                <Route
                    path="/login"
                    element={users ? <Navigate to="/" /> : <Login />}
                />
                <Route
                    path="/post/:id"
                    element={users ? <Post key={posts} posts={posts}/> : <Navigate to="/login" />}      
                />
                <Route
                    path="/:id"
                    element={users ? <Personal key={users} user={users} userdiscord={userdiscord}/> : <Navigate to={`/${users.twitchID}`} />}
                />
                <Route
                    path="/:id/admin"
                    element={users ? <Admin key={users} user={users}  posts={posts}/> : <Navigate to={`/${users.twitchID}/admin`}/> }           
                />
            </Routes>
        </BrowserRouter>
    )
}

export default App;
