import axios from "axios";
import ModalPanel from "../../Pages/ModalPanel/ModalPanel";
// import Load from "../../Components/Load/Load";
import { useEffect, useState } from "react";
// import "./settings_post.css"

const SettingsPost = ({ user, reqPost }) => {
    const [modalActive, setModalActive] = useState(false);
    const [values,  setValue] = useState(null);

    function Toggle(event){
        reqPost.forEach((posted) => {
            if(posted._id === event.target.value){
                setValue(posted);
            };
        });
    };

    function Expere(props){
        const isPost = props.isPost

        return(
            <div>
                <div className="">
                    <button className="" onClick={(event) =>{ setModalActive(true); Toggle(event)}} title="edit" value={isPost._id}>Изменить</button>
                    <button className="" onClick={(event) => {handleClick(event)}} title="delete" value={isPost._id}>Удалить</button>
                </div>
            </div>
        )
    }
    function handleClick(event){
        if(event.target.title === "delete"){
            reqPost.forEach(postes =>{
                if(postes._id === event.target.value){
                    const newPost = {
                        id: postes._id,
                        title: postes.title
                    };
                    axios.post("/api/delete", newPost);
                    window.location.reload();
                };
            });
        };
    };
    return(
        <div>
            <div className="">
                <h1>Последние посты</h1>
                {reqPost.map(post =>(
                    <div className="">
                        <ul className="">
                            <p className="">|| Название поста: {post.title} ||</p>
                            <Expere  isPost={post} />
                        </ul>
                    </div>
                ))}
            </div> 
            {modalActive ? <ModalPanel active={modalActive} setActive ={setModalActive} user={user} posts={values} /> : false} 
        </div>
    )
}

export default SettingsPost;
