import { Link } from "react-router-dom";
import socket from "../../socket";

const ProfileGroup = ({ group, user }) =>{
    async function hadlerClick(event){
        socket.emit("remove_groupe_user", { idGroup: event.target.value, user: user }, (status) =>{
            console.log(status)
            window.location.reload()
        })
    }
    return(
        <div className="">
            <Link className="link" to={`/groups/${group.title}`}>
                <h2>{group.title}</h2>
                
            </Link>
            {group?.players?.find({"players.id": user.userID}) ?(
                <button onClick={hadlerClick} value={group._id} className="card-right">Покинуть</button>
            ):(
                <button onClick={hadlerClick} value={group._id} className="card-right">Вступить</button>
            )}
            <p>Участников:{group.players.length}</p>
        </div>
    )
}

export default ProfileGroup;
