import axios from "axios";
import ModalPanel from "../../Pages/ModalPanel/ModalPanel";
// import Load from "../../Components/Load/Load";
import { useEffect, useState } from "react";
// import "./settings_post.css"

const SettingsPost = ({ user, reqPost }) => {
    console.log(reqPost)
    const [modal, setModal] = useState(false)
    const Toggle = () => setModal(!modal);
    return(
        <div className="">
            <h1>Последние посты</h1>
            {reqPost.map(post =>(
                <div className="">
                    <ul className="">
                        <p className="">|| Название поста: {post.title} ||</p>
                        <button onClick={() => Toggle()}>Добавить</button>
                        <button>Удалить</button>
                    </ul>
                </div>
            ))}
            <ModalPanel show={modal} close={Toggle} post={reqPost}/>
        </div> 
    )
}

export default SettingsPost;
