import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import Avatars from "../../Images/DiscordAvatars.jpg"
import socket from "../../socket.js";
import axios from "axios"
import "./profile.css"
//сделать проверку на то что есть ли req и поменять кнопку на добавлнеие в друзья на галочку
//создать админ палель и там же сделать редактирование сетки + создание постов
const Profile = ({ user }) => {
     const [users_gets, setUserGets] = useState(null)
     const location = useLocation();
     const path = location.pathname.split("/")[1];
     useEffect(() => {
          const getUsers = async () => {
               const res = await axios.get(`/auth?userid=${path}`)
               setUserGets(res.data)
          }
          getUsers()
     },[path]);

     const sendMessage = () => {
          if(path === user.userID){
               socket.emit("edit_profile", {id: path})
          }else{
               socket.emit('add_friend', {id: path, userAddidDb: user._id, socketID: socket.id})
          }
     }
     const GetUser = () => {
          const profile = (
               <div>
                    <div>
                         <img 
                              className=""
                              src={
                                   users_gets.discord.avatar
                                   ? users_gets.discord.avatar
                                   : Avatars
                              }
                              alt=""
                         />
                    </div>
                    <div>
                         <h4 className="">{users_gets.discord.userName}</h4>
                         <span className="">{users_gets.user.desc}</span>
                    </div>
               </div>
          )
          if(users_gets.user.ID === user.ID){
               return(
                    <>
                         {profile}
                         <button onClick={sendMessage}>Редактирование профеля</button>
                    </>
               )
          }else{
               const getFriend = users_gets.user.friends.map(user => user)
               const getRequest = users_gets.user.request.map(user => user)
               console.log(getFriend)
               console.log(getRequest)
               if(getFriend){
                    return(
                         <>
                              {profile}
                              <button onClick={sendMessage}>уже в друзьях</button>
                         </>
                    )
               }
               if(getRequest){
                    return(
                         <>
                         {profile}
                         <button onClick={sendMessage}>Заявка уже отправленна</button>
                         </>
                    )
               }
               return(
                    <>
                         {profile}
                         <button onClick={sendMessage}>Добавть в друзья</button>
                    </>
               )
          }
     }
     if(users_gets === null){
          return(
               <div>Загруза ...</div>
          )
     }
     return(
          <div>
               <GetUser />
               {/* {users_gets.user.ID === user.userID ?(
                    <div>
                         <div>
                              <img 
                                   className=""
                                   src={
                                        users_gets.discord.avatar
                                        ? users_gets.discord.avatar
                                        : Avatars
                                   }
                                   alt=""
                              />
                         </div>
                         <div>
                              <h4 className="">{users_gets.discord.userName}</h4>
                              <span className="">{users_gets.user.desc}</span>
                              <button onClick={sendMessage}>Редактирование профеля</button>
                         </div>
                    </div>
               ):(
                    <div>
                         <div>
                              <img 
                                   className=""
                                   src={
                                        users_gets.discord.avatar
                                        ? users_gets.discord.avatar
                                        : Avatars
                                   }
                                   alt=""
                              />
                         </div>
                         <div>     
                              <h4 className="">{users_gets.discord.userName}</h4>
                              <span className="">{users_gets.user.desc}</span>
                              <button onClick={sendMessage}>Добавть в друзья</button>
                         </div>
                    </div>
               )} */}
          </div>
     )
}

export default Profile;
