import { useEffect, useState } from "react";
import Card from "../Card/Card";
import FriendList from "../../Pages/FrindList/FriendList"
import socket from "../../socket";
import "./home.css"

//создать топ по elo соло
//создать топ команд по elo
const Home = ({ posts }) => {
    const [ user, setUser ] = useState(null)
    const [wordData,setWordData]=useState(null)
    useEffect(() => {
        socket?.on('getNothings', (data) => {
            setUser(data)
        })
    })

    const friendsGets = (users) => {
        return(
            <div className="" key={users._id}>
                <ul>
                    <FriendList friend={users} />
                </ul>
            </div>
        )
    }
    function create(event){
        console.log(event)
    }
    const postes = (post) => {
        return(
            <div className="" key={post._id}>
                <ul className="">
                    <Card posts={post} />
                </ul>
            </div>
        )
    }

    const handleClick=(index)=>{
      console.log(index)
      const wordSlider=posts[index];
      setWordData(wordSlider)
    }
    return(
        <div className="Home">
            <div>
                {user?.friends?.map(users => friendsGets(users))}
            </div>
            <div className="main">
                <p src={wordData?.title} height="300" width="500" /> 
                <div className='flex_row'>
                    {posts?.map((data, i) =>
                        <div className="thumbnail" key={i}>
                            <p className={wordData==i?"clicked":""} src={data.title} onClick={() => handleClick(i)} />
                        </div>
                    )}
                </div>
            </div>
            <div>
                <button onClick={create}>Создать турнир</button>
                {posts?.map((post) => postes(post))}
            </div>
            
        </div>
    )
}

export default Home;
