import Modal from "../../Pages/Modal/Modal"
import { useState, useEffect } from "react";
import "./groups.css"

const Groups = ({ user }) => {
    const [modal, setModal] = useState(false)
    const [group, setGroup] = useState(null);
    useEffect(() => {
        const getGroup = () => {
            fetch(`/api/posts/group/get?userID=${user.userID}`, {
                method: "GET",
                credentials: 'include',
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json",
                    "Access-Control-Allow-Credentials": true,
                }
            }).then((response) => {
                if(response.status === 200) return response.json();
                throw new Error("Ошибка");
            }).then((resObject) => {
                setGroup(resObject.groups);
            }).catch((err) => {
                console.log(err);
            });
        };
        getGroup();
    },[]);

    const Toggle = () => setModal(!modal); 
    
    return(
        <div>
            <div>
                поиск
            </div>
            <div>
                <h3>Группы</h3>
                <div>
                    <button>Ваши Группы</button>
                </div>
                <div>
                    <button>Нати группу</button>
                </div>
                <div>
                    <button onClick={() => Toggle()}>Создать Группу</button>
                </div>
            </div>
            <Modal show={modal} close={Toggle} userName={user.userID}/>
        </div>
    )
};
export default Groups;
