import { useEffect, useState } from "react"

const admin = ["323827486750146561"]

const Panel = ({ user }) => {
    const [modalActive, setModalActive] = useState(false);
    const [values,  setValue] = useState(null);
    useEffect(() => {
        const getPostReq = () => {
            fetch('/api/posts/req/get', {
                method: "GET",
                credentials: 'include',
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json",
                    "Access-Control-Allow-Credentials": true,
                }
            }).then((response) => {
                if(response.status === 200) return response.json();
                throw new Error("Ошибка");
            }).then((resObject) => { 
                    setReqPost(resObject.post_req)
            }).catch((err) => {
                console.log(err);
            });
        };
        getPostReq();
    })

    const GetUser = ({ isUserGet,reqPost }) => {
        const Postes_add = (reqPost) => {
            function handleClick(event){
                if(event.target.title === "delete"){
                    posts.forEach(postes =>{
                        if(postes._id === event.target.value){
                            const newPost = {
                                id: postes._id,
                                title: postes.title
                            };
                            axios.post("/api/delete", newPost);
                            window.location.reload();
                        };
                    });
                };
            };

            return(
                <div className="" key={reqPost._id}>
                    <ul>
                        {reqPost.title}
                        <button className="" onClick={(event) =>{ setModalActive(true); Toggle(event)}} title="edit" value={isPost._id}>Добавить</button>
                        <button className="" onClick={(event) => {handleClick(event)}} title="delete" value={isPost._id}>Удалить</button>
                    </ul>
                </div>
            )
        }
        return(
            <div>
                {reqPost.map(reqPost => Postes_add(reqPost))}
            </div>
        )
    }
    const GuestGreeting = ({ isUserGets }) =>{
        return(
            <>What are doing in my swamp {isUserGets.userName}</>
        )
    }
    function Greeting(props){
        const isLoggedIn = props.isLoggedIn.userID
        for(let i = 0; i < admin.length; i++){
            if(admin[i] === isLoggedIn){
                if(reqPost !== null){
                    return <GetUser isUserGet={user} reqPost={reqPost} />
                }
            }
        }
        return <GuestGreeting isUserGets={user} />;
    }
    return(
        <>
            <Greeting isLoggedIn={user} />
        </>
    )
}

export default Panel;
