import { useLocation } from "react-router";
import "./post.css"

const Post = ({ posts }) => {
    const location = useLocation();
    const path = location.pathname.split("/")[2];

    const Postes = (postes) => {
        if(postes._id === path){
            console.log(postes)
            return(
                <div className="post" key={postes._id}>
                    <div class="group-image">
                        <img src="assets/group-img.jpg" alt="Group Image" />
                    </div>
                    <div class="group-info">
                        <p class="group-description">{postes.contex}</p>
                        <p class="group-time">Время проведения: 12:00 - 14:00</p>
                    </div>
                    <div class="group-buttons">
                        <button class="button">Принять</button>
                    </div>
                </div>
            )
        };
    };
    return(
        <div>
            {posts.map(postes => Postes(postes))}
        </div>
    )
};
export default Post;
