import { useEffect, useState } from "react";
import Card from "../Card/Card";
import socket from "../../socket";
import "./home.css"


const Home = ({ posts }) => {
    const [ user, setUser ] = useState(null)
    const [wordData,setWordData]=useState(posts)

    useEffect(() => {
        socket?.on('getNothings', (data) => {
            setUser(data)
        })
    })

    const userGetsInfo = ({ userNames, avatars }) => {
        console.log(userNames, avatars)
    }
    const postes = (post, index) => {
        const slider = posts[index]
        setWordData(slider)
        return(
            <div className="" key={post._id}>
                <ul className="">
                    <Card posts={slider} />
                </ul>
            </div>
        )
    }
    
    return(
        <div>
            {user?.friends?.map(users => userGetsInfo(users))}
            {posts?.map((post,i) => postes(post, i))}
        </div>
    )
}

export default Home;
