import { Link } from "react-router-dom";
import "./navbar.css"
import Profile from "../PersonalAc/PersonalAc";
const Navbar = ({ user }) => {
    const Logout = () => {
        window.open(`${process.env.REACT_APP_REDIRECT}/auth/logout`, "_self")
    }

    function GetUser(props){
        const isGetUser = props.isGetUser
        console.log(isGetUser)
        if(isGetUser === undefined){
            return( 
                <ul className="list">
                    <li className="listItem">
                        <Link className="link" to="login">
                            Login
                        </Link>
                    </li>
                </ul>
            )
        }else {
            return (
                <ul className="list">
                    <li className="listItem">
                        <img 
                            src={isGetUser.avatar}
                            alt=""
                            className="avatar"
                        />
                    </li>
                    <li className="listItem">
                        {Array(isGetUser).map(user =>(
                            <Profile key={user} user={user}/>
                        ))}
                    </li>
                    <li className="listItem" onClick={Logout}>
                        Logout
                    </li>
                 </ul>
    
            )
        }
    }
    return(
        <div className="navbar">
            <span className="logo">
                <Link className="link" to="/">
                    Twitch Revels
                </Link>
            </span>
            <GetUser isGetUser={user}/>
        </div>
    )
}

export default Navbar;
