import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import axios from "axios"
import "./profile.css"

const Profile = ({ user }) => {
     const [users_gets, setUserGets] = useState(null) 
     const location = useLocation();
     const path = location.pathname.split("/")[1];
     axios.post("/api/gets_user", path)
     useEffect(() => {
          const userGetsInfo = () => {
               fetch(`/auth/login/${path}`, {
                    method: "GET",
                    credentials: 'include',
                    headers: {
                        "Accept": "application/json",
                        "Content-Type": "application/json",
                        "Access-Control-Allow-Credentials": true,
                    }
                }).then((response) => {
                    if(response.status === 200) return response.json()
                    throw new Error("Ошибка")
                }).then((resObject) => {
                    setUserGets(resObject.user)
                }).catch((err) => {
                    console.log(err)
                })
          }
          userGetsInfo()
     }, [])
     return(
          <div>сделать профиль на подобия стима и тут же сделать на редактирование описания{user.userName}</div>
     )
}

export default Profile;
