import Profile from "../../Pages/Profile/Profile";
import Discord from "../../Images/DiscordAvatars.jpg"
import { useEffect, useState } from "react";
import socket from "../../socket.js";
import { Link } from "react-router-dom";
import "./navbar.css"

const Navbar = ({ user }) => {
    const Logout = () => {
        window.open(`${process.env.REACT_APP_REDIRECT}/auth/logout`, "_self")
    }
    const [notification, setNotification] = useState(null);
    useEffect(() => {
        const post_nothings = () => {
            if(user !== undefined){
                socket.emit('post_nothings', user.userID)
            }
        }
        const getNothings = () => {
            socket.on('getNothings', (data) => {
                setNotification(data)
            })
        },
        iterval = setInterval(() => {
            post_nothings()
            getNothings()
        },1000)
        return () => {
            clearInterval(iterval)
        }
    })

    function GetUser(props){
        const isGetUser = props.isGetUser


        const displayNotification = (n) => {
            console.log(n)
        }
        console.log(notification)
        if(isGetUser === undefined){
            return( 
                <ul className="list">
                    <li className="listItem">
                        <Link className="link" to="login">
                            Login
                        </Link>
                    </li>
                </ul>
            )
        }else {
            return (
                <ul className="list">
                    <li className="listItem">
                        <div className="listItem">
                            <div>
                                {}
                            </div>
                        </div>
                        {isGetUser.avatar === null ? (
                            <img 
                                src={Discord}
                                alt=""
                                className="avatar"
                            />
                        ):(
                            <img 
                                src={isGetUser.avatar}
                                alt=""
                                className="avatar"
                            />
                        )}
                    </li>
                    <li className="listItem">
                        {Array(isGetUser).map(user =>(
                            <Profile key={user} user={user} />
                        ))}
                    </li>
                    <li className="listItem" onClick={Logout}>
                        Logout
                    </li>
                 </ul>
    
            )
        }
    }
    return(
        <div className="navbar">
            <span className="logo">
                <Link className="link" to="/">
                    Twitch Revels
                </Link>
            </span>
            <GetUser isGetUser={user}/>
        </div>
    )
}

export default Navbar;
