// import axios from "axios";
// import ModalPanel from "../../Components/ModalPanel/ModalPanel";
// import Load from "../../Components/Load/Load";
// import { useEffect, useState } from "react";
// import "./settings_post.css"

const SettingsPost = ({ user, reqPost }) => {

    function Toggle(event){
        posts.forEach((posted) => {
            if(posted._id === event.target.value){
                setValue(posted);
            };
        });
    };

    function Expere(props){
        const isPost = props.isPost
        const [visible, setVisible] = useState(true);

        useEffect(() => {
            const timer = setTimeout(() => {
                setVisible(false)
            }, props.delay);
            return () => clearTimeout(timer);
        }, [props.delay])
        return(
            <div>
                {visible ? (
                    <div className="">
                        Загрузка...
                    </div> 
                ):(
                    <div className="">
                        <button className="" onClick={(event) =>{ setModalActive(true); Toggle(event)}} title="edit" value={isPost._id}>Изменить</button>
                        <button className="" onClick={(event) => {handleClick(event)}} title="delete" value={isPost._id}>Удалить</button>
                    </div>
                )}
            </div>
        )
    }
    function handleClick(event){
        if(event.target.title === "delete"){
            posts.forEach(postes =>{
                if(postes._id === event.target.value){
                    const newPost = {
                        id: postes._id,
                        title: postes.title
                    };
                    axios.post("/api/delete", newPost);
                    window.location.reload();
                };
            });
        };
    };
    return(
        <div className="">
            <h1>Последние посты</h1>
            {reqPost.map(post =>(
                <div className="">
                    <ul className="">
                        <p className="">|| Название поста: {post.title} || автор: {post.author}||</p>
                        <Expere delay="10000" isPost={post} />
                    </ul>
                </div>
            ))}
        </div>  
    )
}

export default SettingsPost;
