import { Link } from "react-router-dom";
// import PersonalAc from "../../Pages/Profile"
const Navbar = ({ user }) => {
    const Logout = () => {
        window.open(`${process.env.REACT_APP_REDIRECT}/auth/logout`, "_self")
    }

    function GetUser(props){
        if(props.isGetUser === null){
            return( 
                <ul className="list">
                    <li className="listItem">
                        <Link className="link" to="login">
                            Login
                        </Link>
                    </li>
                </ul>
            )
        }else {
            return (
                <ul className="list">
                    <li className="listItem">
                        <img 
                            src={isGetUser.avatars_on_twitch}
                            alt=""
                            className="avatar"
                        />
                    </li>
                    <li className="listItem">
                        {isGetUser.username_on_twitch}
                    </li>
                    <li className="listItem">
                        {Array(isGetUser).map(user =>(
                            <PersonalAc key={user} user={user}/>
                        ))}
                    </li>
                    <li className="listItem toggle_btn" onClick={() => setDarkMode(!isDarkMode)}>
                        {isDarkMode? (
                            <BsSun color="#ff0" size="24" title="Switch to light mode" />
                        ):(
                            <BsMoon size="24" title="Switch to dark mode" />
                        )}
                    </li>
                    <li className="listItem" onClick={logout}>
                        Logout
                    </li>
                 </ul>
    
            )
        }
    }
    return(
        <ul className="list">
            <span className="logo">
                <Link className="link" to="/">
                    Twitch Revels
                </Link>
            </span>
            <GetUser isGetUser={user}/>
        </ul>
    )
}

export default Navbar;
