import axios from "axios";
import Modal from "../../Components/Modal/Modal";
import Load from "../../Components/Load/Load";
import { useEffect, useState } from "react";
import "./settings_post.css"

const SettingsPost = ({ user, posts }) => {    

    const [modalActive, setModalActive] = useState(false);
    const [values,  setValue] = useState(null);
    posts.forEach((post) => {
        if((post.author === '') && (post.content === '') && (post.context_desc === '') && (post.title === '')){
            return(
                <div>
                    <Load />
                </div>
            )
        }
    })

    function Toggle(event){
        posts.forEach((posted) => {
            if(posted._id === event.target.value){
                setValue(posted);
            };
        });
    };

    function Expere(props){
        const isPost = props.isPost
        const [visible, setVisible] = useState(true);

        useEffect(() => {
            const timer = setTimeout(() => {
                setVisible(false)
            }, props.delay);
            return () => clearTimeout(timer);
        }, [props.delay])
        return(
            <div>
                {visible ? (
                    <div className="">
                        <Load />
                    </div> 
                ):(
                    <div className="">
                        <button className="" onClick={(event) =>{ setModalActive(true); Toggle(event)}} title="edit" value={isPost._id}>Изменить</button>
                        <button className="" onClick={(event) => {handleClick(event)}} title="delete" value={isPost._id}>Удалить</button>
                    </div>
                )}
            </div>
        )
    }
    
    function handleClick(event){
        if(event.target.title === "delete"){
            posts.forEach(postes =>{
                if(postes._id === event.target.value){
                    const newPost = {
                        id: postes._id,
                        title: postes.title
                    };
                    axios.post("/api/delete", newPost);
                    window.location.reload();
                };
            });
        };
    };

    return(
        <div className="">
            <div className="">
                <h1>Последние посты</h1>
                {posts.map(post =>(
                    <div className="">
                        <ul className="">
                            <p className="">|| Название поста: {post.title} || автор: {post.author}||</p>
                            <Expere delay="10000" isPost={post} />
                        </ul>
                    </div>
                ))}
            </div>
            {modalActive ? <Modal active={modalActive} setActive ={setModalActive} user={user} posts={values} /> : false}
        </div>
    )
}

export default SettingsPost;
