import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const GroupsProfile = ({ user }) => {
    const location = useLocation()
    const path = location.pathname.split("/")[2];
    const [group, setGroup] = useState(null);
    useEffect(() => {
        const getGroup = () => {
            fetch(`/api/posts/group/get?userID=${user.userID}`, {
                method: "GET",
                credentials: 'include',
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json",
                    "Access-Control-Allow-Credentials": true,
                }
            }).then((response) => {
                if(response.status === 200) return response.json();
                throw new Error("Ошибка");
            }).then((resObject) => {
                resObject.groups.forEach(group => {
                    if(group.title === path){
                        setGroup(group);
                    }
                });
            }).catch((err) => {
                console.log(err);
            });
        };
        getGroup();
    },[user]);
    console.log(group)
    return( 
        <div class="groupes-card">
            <div class="groupes-names-card card">
                <p class="groupes-name">{group?.title}</p> 
                <p1 class="groupes-member-count">{group?.players.lenght}</p1>
            </div>
            <div class="groupes-description-card card">
                 <h3>Описание группы:</h3>
                <p class="groupes-description">Описание группы:</p>
            </div>
            <div class="groupes-avatars-card card">
                <h3>Участники группы:</h3>
                <ul class="groupes-avatars">

                </ul>
            </div>
            <div class="groupes-leave-button-c">
                <button class="groupes-leave-button">Покинуть группу</button>
            </div>
        </div>
    )
}

export default GroupsProfile;
