import { useEffect, useState } from "react";
import { useParams, useLocation } from "react-router";
import axios from "axios"
import "./profile.css"

const Profile = ({ user }) => {
    const [users_gets, setUserGets] = useState(null) 
    const location = useLocation();
    const path = location.pathname.split("/")[1];
    useEffect(() => {
        const getUsers = async () => {
            const res = await axios.get(`/api?userid=${path}`)
            setUserGets(res.data)
        }
        getUsers()
    },[path])
    console.log(users_gets)
    if(users_gets !== null){
        return(
            <div>
                {users_gets.user.ID}
            </div>
        )
    }
    return(
        <div>сделать профиль на подобия стима и тут же сделать на редактирование описания{user.userName}</div>
    )
}

export default Profile;
